mapper.select:
select
from T_CONTRACT_USER_USERINFO T_CONTRACT_USER_USERINFO LEFT JOIN T_CONTRACT_SYS_DEPART T_CONTRACT_SYS_DEPART ON T_CONTRACT_USER_USERINFO.DEPARTMENT=T_CONTRACT_SYS_DEPART.BM AND T_CONTRACT_SYS_DEPART.STATUS=1 LEFT JOIN T_CONTRACT_SYS_DEPART T_CONTRACT_SYS_TEAM ON T_CONTRACT_USER_USERINFO.TEAM=T_CONTRACT_SYS_TEAM.BM AND T_CONTRACT_SYS_TEAM.STATUS=1
AND T_CONTRACT_USER_USERINFO.WID=#{wid,jdbcType=VARCHAR}
AND (T_CONTRACT_USER_USERINFO.NAME1 LIKE '%${name1}%' or T_CONTRACT_USER_USERINFO.GH LIKE '%${gh}%')
AND datastatus=#{datastatus,jdbcType=INTEGER}
ORDER BY GH
